import React, { useState, useEffect } from 'react';
import PhotoUpload from './components/PhotoUpload';
import axios from 'axios';

function App() {
  const [imageURL, setImageURL] = useState(null);

  // Функция для установки URL загруженной фотографии
  const setUploadedPhotoURL = (url) => {
    setImageURL(url);
  };

  useEffect(() => {
    // Загрузите фотографию из папки "images" на сервере
    const fetchImage = async () => {
      try {
        const response = await axios.get('images/4.jpg'); // Замените на фактический URL и имя файла
        setImageURL(response.config.url);
      } catch (error) {
        console.error('Ошибка при получении изображения', error);
      }
    };

    fetchImage();
  }, []);

  return (
    <div className="App">
      <h1>Моя загрузка фотографий</h1>
      <PhotoUpload setUploadedPhotoURL={setUploadedPhotoURL} />
      {imageURL && <img src={imageURL} alt="Загруженное изображение" />}
    </div>
  );
}

export default App;
