import React, { useState } from 'react';
import axios from 'axios';

function PhotoUpload({ setUploadedPhotoURL }) {
  const [selectedFile, setSelectedFile] = useState(null);

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    setSelectedFile(file);
  };

  const handleUpload = async () => {
    if (selectedFile) {
      const formData = new FormData();
      formData.append('file', selectedFile);

      try {
        // Отправьте выбранный файл на сервер FastAPI
        const response = await axios.post('http://localhost:8000/upload/', formData, {
          headers: {
            'Content-Type': 'multipart/form-data',
          },
        });

        // Получите URL загруженной фотографии из ответа и передайте его в родительский компонент
        const imageURL = response.data.image_url;
        setUploadedPhotoURL(imageURL);

        // Сбросьте состояние выбранного файла
        setSelectedFile(null);
      } catch (error) {
        console.error('Ошибка при загрузке изображения', error);
      }
    }
  };

  return (
    <div>
      <input type="file" accept="image/*" onChange={handleFileChange} />
      <button onClick={handleUpload}>Загрузить фото</button>
    </div>
  );
}

export default PhotoUpload;
