import React, { useState } from 'react';
import axios from 'axios';
import Header from './components/Header';
import './style.css';

function App() {
  const [imageURL, setImageURL] = useState(null);
  const [selectedFile, setSelectedFile] = useState(null);

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    setSelectedFile(file);
  };

  const handleUpload = async () => {
    const formData = new FormData();
    formData.append('file', selectedFile);

    try {
      const response = await axios.post('http://localhost:8000/upload/', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      const imageURL = response.data.image_url;
      setImageURL(imageURL);
      setSelectedFile(null);
    } catch (error) {
      console.error('Ошибка при загрузке изображения', error);
    }
  };

  return (
    <div className="App">
      <Header />
      <div className="rectangle-container">
        <div className="rectangles">
          <div className="rectangle rectangle-text">
            <p className="p_help">БДИТЕЛЬНОСТЬ ГРАЖДАН – ОСНОВА ПРОТИВОДЕЙСТВИЯ ТЕРРОРИЗМУ
            </p>
            <p className="p_help2"> Пожалуйста, проявите бдительность! Если вы заметили человека с оружием, то сфоткайте его и позвоните в полицию! По телефону 102
            </p>
          </div>
          <div className="rectangle rectangle-button">
            <label className="upload-label" htmlFor="fileInput">


               <img src={'http://localhost:8000' + imageURL} alt="Загруженное изображение" style={{ width: '50%' }} />
            </label>

          </div>
<input type="file" id="fileInput" accept="image/*" onChange={handleFileChange} />
      <p className="upload-text">Выберите файл и загрузите</p>
            <button className="upload-button" onClick={handleUpload}>Загрузить</button>
        </div>

      </div>

      {imageURL && (
        <div>

        </div>
      )}
    </div>
  );
}

export default App;
