import React, { useState } from 'react';

function PhotoUpload() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [imageURL, setImageURL] = useState(null);

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    setSelectedFile(file);
  };

  const handleUpload = async () => {
    const formData = new FormData();
    formData.append('file', selectedFile);

    // Отправьте выбранный файл на сервер с помощью запроса API
    // Здесь вы должны использовать библиотеку, такую как axios, для отправки запроса POST на ваш FastAPI сервер

    // После успешной загрузки, установите URL изображения в состояние
    setImageURL(URL.createObjectURL(selectedFile));
  };

  return (
    <div>
      <input type="file" accept="image/*" onChange={handleFileChange} />
      <button onClick={handleUpload}>Загрузить фото</button>
      {imageURL && <img src={imageURL} alt="Загруженное изображение" />}
    </div>
  );
}

export default PhotoUpload;
