import React, { useState } from 'react';
import PhotoUpload from './components/PhotoUpload';
import axios from 'axios';

function App() {
  const [imageURL, setImageURL] = useState(null);

  // Функция для установки URL загруженной фотографии
  const setUploadedPhotoURL = (url) => {
    setImageURL(url);
  };

  const handleUploadImage = async () => {
    const formData = new FormData();
    try {
      const response = await axios.post('http://localhost:8000/upload/', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      const imageURL = response.data.image_url;
      setUploadedPhotoURL(imageURL);
    } catch (error) {
      console.error('Ошибка при загрузке изображения', error);
    }
  };

  return (
    <div className="App">
      <h1>Моя загрузка фотографий</h1>
      <PhotoUpload setUploadedPhotoURL={setUploadedPhotoURL} />
      {imageURL && (
        <div>
          <img src={imageURL} alt="Загруженное изображение" />
        </div>
      )}
      <button onClick={handleUploadImage}>Загрузить изображение на сайт</button>
    </div>
  );
}

export default App;
