import React, { useState, useEffect } from 'react';
import PhotoUpload from './components/PhotoUpload';
import axios from 'axios';
import Header from './components/Header'; // Импорт компонента Header

function App() {
  const [imageURL, setImageURL] = useState(null);

  useEffect(() => {
    // Получить URL изображения с сервера и установить его в состояние
    axios.get('/images/your_image_filename.jpg')
      .then(response => {
        setImageURL(response.config.url);
      })
      .catch(error => {
        console.error('Ошибка при получении изображения', error);
      });
  }, []);

  return (
    <div className="App">
      <Header /> {/* Добавление шапки */}
      <PhotoUpload />
      {imageURL && <img src={imageURL} alt="Загруженное изображение" />}
    </div>
  );
}

export default App;
