import React, { useState, useEffect } from 'react';
import axios from "axios";

function PhotoUpload() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [imageURL, setImageURL] = useState(null);

  useEffect(() => {
    // При запуске приложения проверяем наличие сохраненного изображения в локальном хранилище
    const savedImage = localStorage.getItem('uploadedImage');
    if (savedImage) {
      setImageURL(savedImage);
    }
  }, []);

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    setSelectedFile(file);

    // Сохраняем выбранный файл в локальное хранилище
    const reader = new FileReader();
    reader.onload = (e) => {
      const imageData = e.target.result;
      localStorage.setItem('uploadedImage', imageData);
      setImageURL(imageData);
    };
    reader.readAsDataURL(file);
  };

  const handleUpload = async () => {
  if (selectedFile) {
    const formData = new FormData();
    formData.append('file', selectedFile);

    try {
      // Отправьте выбранный файл на сервер с использованием Axios или другой библиотеки
      await axios.post('/upload/', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      // После успешной загрузки, сбросьте состояние выбранного файла
      setSelectedFile(null);
    } catch (error) {
      console.error('Ошибка при загрузке изображения', error);
    }
  }
};


  return (
    <div>
      <input type="file" accept="image/*" onChange={handleFileChange} />
      <button onClick={handleUpload}>Загрузить фото</button>
      {imageURL && <img src={imageURL} alt="Загруженное изображение" />}
    </div>
  );
}

export default PhotoUpload;
