// app.js
import React, { useState, useEffect } from 'react';
import PhotoUpload from './components/PhotoUpload';
import axios from 'axios';
import Header from './components/Header'; // Путь к вашему компоненту Header


function App() {
  const [imageURL, setImageURL] = useState(null);

  // useEffect(() => {
  //   // Функция для загрузки фотографии
  //   const loadImage = async () => {
  //     try {
  //       // Выполняем GET-запрос к FastAPI, чтобы получить URL загруженной фотографии
  //       const response = await axios.get('http://localhost:8000/get_image'); // Замените на соответствующий URL
  //       const image_url = response.data.image_url;
  //
  //       // Устанавливаем URL фотографии в состояние
  //       setImageURL(image_url);
  //     } catch (error) {
  //       console.error('Ошибка при загрузке изображения', error);
  //     }
  //   };
  //
  //   // Вызываем функцию для загрузки фотографии при загрузке страницы
  //   loadImage();
  // }, []);

    const [selectedFile, setSelectedFile] = useState(null);

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    setSelectedFile(file);
  };

  const handleUpload = async () => {
    const formData = new FormData();
    formData.append('file', selectedFile);

    try {
      const response = await axios.post('http://localhost:8000/upload/', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      const imageURL = response.data.image_url;
      setImageURL(imageURL);
      setSelectedFile(null);
    } catch (error) {
      console.error('Ошибка при загрузке изображения', error);
    }
  };

  return (
    <div className="App">
      <h1>Моя загрузка фотографий</h1>
      <div>
      <input type="file" accept="image/*" onChange={handleFileChange} />
      <button onClick={handleUpload}>Загрузить фото</button>
    </div>
      {imageURL && (
        <div>
          <img src={'http://localhost:8000' + imageURL} alt="Загруженное изображение"  style={{ width: '50%' }}/>
        </div>
      )}
    </div>
  );
}

export default App;
