import React, { useState } from 'react';
import axios from 'axios';
import Header from './components/Header';
import './style.css';

function App() {
  const [imageURL, setImageURL] = useState(null);
  const [selectedFile, setSelectedFile] = useState(null);

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    setSelectedFile(file);
  };

  const handleUpload = async () => {
    const formData = new FormData();
    formData.append('file', selectedFile);

    try {
      const response = await axios.post('http://localhost:8000/upload/', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      const imageURL = response.data.image_url;
      setImageURL(imageURL);
      setSelectedFile(null);
    } catch (error) {
      console.error('Ошибка при загрузке изображения', error);
    }
  };

  return (
    <div className="App">
      <Header />
      <div className="rectangle-container">
        <div className="rectangle">
          <p className="p_help">
            Пожалуйста, проявите бдительность! Если вы заметили человека с оружием, то сфоткайте его и позвоните в полицию! По телефону 102
          </p>
        </div>
        <div className="rectangle" style={{ backgroundColor: '#2474F6' }}>
          <p style={{ color: 'white', fontSize: '24px' }}>
            {/* The text content here */}
          </p>
        </div>
      </div>
      {imageURL && (
        <div>
          <img src={'http://localhost:8000' + imageURL} alt="Загруженное изображение" style={{ width: '50%' }} />
        </div>
      )}
    </div>
  );
}

export default App;
